 { scoreA++; } if (b.name.toLowerCase().includes(query)) { scoreB++; } if (b.genre.toLowerCase().includes(query)) { scoreB++; } // Сортировка по score (убывающая) return scoreB - scoreA; }); // Вызываем callback-функцию с результатами поиска return callback(null, results); } }); } app.use((req, res, next) => { if (req.session.musicianId) { getMusicianById(req.session.musicianId, (err, musician) => { if (err) { console.error(\\"Ошибка при получении current user: \\", err); res.locals.userLoggedIn = false; } else { res.locals.musician = musician; res.locals.userLoggedIn = true; res.locals.username = musician.name; } next(); }); } else { res.locals.userLoggedIn = false; next(); } }); app.get(\\"/\\", (req, res) => { getLastNRegisteredMusicians(5, (err, lastRegisteredMusicians) => { if (err) { console.error(\\"Ошибка при получении последних зарегистрированных музыкантов: \\", err); res.status(500).send(\\"Ошибка получения данных\\"); } else { res.render(\\"index\\", { musicians: lastRegisteredMusicians, query:'',city:'',role:'',genre:'',tracks:tracks}); } }); }); app.get(\\"/autocomplete/cities\\", async (req, res) => { const searchString = req.query.term; connection.query( \\"SELECT DISTINCT city FROM users WHERE city LIKE ?\\", [searchString + '%'], (error, results, fields) => { if (error) { console.error(\\"Ошибка выполнения запроса: \\", error); res.status(500).send(\\"Ошибка выполнения запроса\\"); } else { const cities = results.map(row => row.city); res.json(cities); } } ); }); app.get(\\"/register\\", (req, res) => { if (req.session.musicianId) { const musician = getMusicianById(req.session.musicianId); res.redirect(\\"/profile/\\" + musicianId); } else { res.render(\\"register\\", { citiesAndRegions, city:'', query:'',role:'' }); } }); app.post(\\"/register\\", (req, res) => { if (req.session.musicianId) { getMusicianById(req.session.musicianId, (err, musician) => { if (err) { console.error(\\"Ошибка при получении музыканта: \\", err); } else { res.redirect(\\"