///////////////////////////////*/ /// @notice emitted when updating the maximum amount of delegates per user event MaxDelegatesUpdate(uint256 oldMaxDelegates, uint256 newMaxDelegates); /// @notice emitted when updating the canContractExceedMaxDelegates flag for an account event CanContractExceedMaxDelegatesUpdate( address indexed account, bool canContractExceedMaxDelegates ); /// @notice the maximum amount of delegates for a user at a given time uint256 public maxDelegates; /// @notice an approve list for contracts to go above the max delegate limit. mapping(address => bool) public canContractExceedMaxDelegates; /// @notice set the new max delegates per user. Requires auth by `authority`. function _setMaxDelegates(uint256 newMax) internal { uint256 oldMax = maxDelegates; maxDelegates = newMax; emit MaxDelegatesUpdate(oldMax, newMax); } /// @notice set the canContractExceedMaxDelegates flag for an account. function _setContractExceedMaxDelegates( address account, bool canExceedMax ) internal { require( !canExceedMax || account.code.length != 0, \\"ERC20MultiVotes: not a smart contract\\" ); // can only approve contracts canContractExceedMaxDelegates[account] = canExceedMax; emit CanContractExceedMaxDelegatesUpdate(account, canExceedMax); } /*/////////////////////////////////////////////////////////////// DELEGATION LOGIC //////////////////////////////////////////////////////////////*/ /// @dev Emitted when a `delegator` delegates `amount` votes to `delegate`. event Delegation( address indexed delegator, address indexed delegate, uint256 amount ); /// @dev Emitted when a `delegator` undelegates `amount` votes from `delegate`. event Undelegation( address indexed delegator, address indexed delegate, uint256 amount ); /// @dev Emitted when a token transfer or delegate change results in changes to an account\'s voting power. event DelegateVotesChanged( address indexed delegate, uint256 previousBalance, uint256 newBalance ); /// @notice An event that is emitted when an account changes its delegate /// @dev this is used for backward compatibility with OZ interfaces for ERC20Votes and ERC20VotesComp. event DelegateChanged( address indexed delegator, address indexed fromDelegate, address indexed toDelegate ); /// @notice mapping from a delegator and delegatee to the delegated amount. mapping(address => mapping(address => uint256)) private _delegatesVotesCount; /// @notice mapping from a delegator to the total number of delegated votes. mapping(address => uint256) public userDelegatedVotes; /// @notice list of delegates per user. mapping(address => EnumerableSet.AddressSet) private _delegates; /** * @notice Get the amount of votes currently delegated by `delegator` to `delegatee`. * @param delegator the account which is delegating votes to `delegatee`. * @param delegatee the account receiving votes from `delegator`. * @return the total amount of votes delegated to `delegatee` by `delegator` */ function delegatesVotesCount( address delegator, address delegatee ) public view virtual returns (uint256) { return _delegatesVotesCount[delegator][delegatee]; } /** * @notice Get the list of delegates from `delegator`. * @param delegator the account which is delegating votes to delegates. * @return the list of delegated accounts. */ function delegates( address delegator ) public view returns (address[] memory) { return _delegates[delegator].values(); } /** * @notice Checks whether delegatee is in the `delegator` mapping. * @param delegator the account which is delegating votes to delegates. * @param delegatee the account which receives votes from delegate. * @return true or false */ function containsDelegate( address delegator, address delegatee ) public view returns (bool) { return _delegates[delegator].contains(delegatee); } /** * @notice Get the number of delegates from `delegator`. * @param delegator the account which is delegating votes to delegates. * @return the number of delegated accounts. */ function delegateCount(address delegator) public view returns (uint256) { return _delegates[delegator].length(); } /** * @notice Delegate `amount` votes from the sender to `delegatee`. * @param delegatee the receiver of votes. * @param amount the amount of votes received. * @dev requires \\"freeVotes(msg.sender) > amount\\" and will not exceed max delegates */ function incrementDelegation( address delegatee, uint256 amount ) public virtual { _incrementDelegation(msg.sender, delegatee, amount); } /** * @notice Undelegate `amount` votes from the sender from `delegatee`. * @param delegatee the receivier of undelegation. * @param amount the amount of votes taken away. */ function undelegate(address delegatee, uint256 amount) public virtual { _undelegate(msg.sender, delegatee, amount); } /** * @notice Delegate all votes `newDelegatee`. First undelegates from an existing delegate. If `newDelegatee` is zero, only undelegates. * @param newDelegatee the receiver of votes. * @dev undefined for `delegateCount(msg.sender) > 1` * NOTE This is meant for backward compatibility with the `ERC20Votes` and `ERC20VotesComp` interfaces from OpenZeppelin. */ function delegate(address newDelegatee) external virtual { _delegate(msg.sender, newDelegatee); } function _delegate( address delegator, address newDelegatee ) internal virtual { uint256 count = delegateCount(delegator); // undefined behavior for delegateCount > 1 require(count < 2, \\"ERC20MultiVotes: delegation error\\"); address oldDelegatee; // if already delegated, undelegate first if (count == 1) { oldDelegatee = _delegates[delegator].at(0); _undelegate( delegator, oldDelegatee, _delegatesVotesCount[delegator][oldDelegatee] ); } // redelegate only if newDelegatee is not empty if (newDelegatee != address(0)) { _incrementDelegation(delegator, newDelegatee, freeVotes(delegator)); } emit DelegateChanged(delegator, oldDelegatee, newDelegatee); } function _incrementDelegation( address delegator, address delegatee, uint256 amount ) internal virtual { // Require freeVotes exceed the delegation size uint256 free = freeVotes(delegator); require( delegatee != address(0) && free >= amount, \\"ERC20MultiVotes: delegation error\\" ); bool newDelegate = _delegates[delegator].add(delegatee); // idempotent add require( !newDelegate || delegateCount(delegator) <= maxDelegates || canContractExceedMaxDelegates[delegator], \\"ERC20MultiVotes: delegation error\\" ); _delegatesVotesCount[delegator][delegatee] += amount; userDelegatedVotes[delegator] += amount; emit Delegation(delegator, delegatee, amount); _writeCheckpoint(delegatee, _add, amount); } function _undelegate( address delegator, address delegatee, uint256 amount ) internal virtual { uint256 newDelegates = _delegatesVotesCount[delegator][delegatee] - amount; if (newDelegates == 0) { require(_delegates[delegator].remove(delegatee)); } _delegatesVotesCount[delegator][delegatee] = newDelegates; userDelegatedVotes[delegator] -= amount; emit Undelegation(delegator, delegatee, amount); _writeCheckpoint(delegatee, _subtract, amount); } function _writeCheckpoint( address delegatee, function(uint256, uint256) view returns (uint256) op, uint256 delta ) private { Checkpoint[] storage ckpts = _checkpoints[delegatee]; uint256 pos = ckpts.length; uint256 oldWeight = pos == 0 ? 0 : ckpts[pos - 1].votes; uint256 newWeight = op(oldWeight, delta); if (pos > 0 && ckpts[pos - 1].fromBlock == block.number) { ckpts[pos - 1].votes = newWeight.safeCastTo224(); } else { ckpts.push( Checkpoint({ fromBlock: block.number.safeCastTo32(), votes: newWeight.safeCastTo224() }) ); } emit DelegateVotesChanged(delegatee, oldWeight, newWeight); } function _add(uint256 a, uint256 b) private pure returns (uint256) { return a + b; } function _subtract(uint256 a, uint256 b) private pure returns (uint256) { return a - b; } /*/////////////////////////////////////////////////////////////// ERC20 LOGIC //////////////////////////////////////////////////////////////*/ /// NOTE: any \\"removal\\" of tokens from a user requires freeVotes(user)